import React from 'react';
const Header = () => {
    var link1 = document.querySelector(".menu:nth-child(1)");
    var link2 = document.querySelector(".menu:nth-child(2)");
    var link3 = document.querySelector(".menu:nth-child(3)");
    var link4 = document.querySelector(".menu:nth-child(4)");
    const activate = () => {

        link1.classList.add("active");
        link2.classList.remove("active");
        link3.classList.remove("active");
        link4.classList.remove("active");

    }
    return (
        <nav className="navbar">
            <div className="container">
                <div className="nav-elements">
                    <ul className='menu'>
                        <li >
                            <a href="/" className='nav-link' onClick={activate}>Accueil</a>
                        </li>
                        <li>
                            <a href="/Galerie" className='nav-link' onClick={activate}>Galerie</a>
                        </li>
                        <li>
                            <a href="/Tarifs_et_Prestations" className='nav-link' onClick={activate}>tarifs et prestations</a>
                        </li>
                        <li>
                            <a href="/Contact" className='nav-link' onClick={activate} >Contact</a>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    )

}
export default Header
