import React from 'react';
import { NavLink } from 'react-router-dom';

export const Header = () => {
    const navLinks = document.querySelector(".nav-links")

    const handleClick = () => {
        navLinks.classList.toggle('mobile-menu')

    };
    return (
        <nav className="navbar">
            <div className="nav-links">
                <ul>
                    <li >
                        <NavLink to="/" className={({ isActive }) => (isActive ? 'active' : '')}>Home</NavLink>
                    </li>
                    <li>
                        <NavLink to="/Galerie" className={({ isActive }) => (isActive ? 'active' : '')}>Galery</NavLink>
                    </li>
                    <li>
                        <NavLink to="/Tarifs_et_Prestations" className={({ isActive }) => (isActive ? 'active' : '')}>Price</NavLink>
                    </li>
                    <li>
                        <NavLink to="/Contact" className={({ isActive }) => (isActive ? 'active' : '')}>Contact</NavLink>
                    </li>
                </ul>
            </div>
            <button onClick={handleClick} className='menu-hamburger'></button>
        </nav>
    )
}
export default Header;
