import React from 'react';


export const Header = () => {
    const navLinks = document.querySelector(".nav-links")
    const handleClick = () => {
        navLinks.classList.toggle('mobile-menu')

    };
    const menuClick = () => {
        $(this).addClass("active").siblings().removeClass("active");
    }
    return (
        <nav className="navbar">
            <div className="nav-links">
                <ul>
                    <li >
                        <a href="/" onClick={menuClick} >Home</a>
                    </li>
                    <li>
                        <a href="/Galerie" onClick={menuClick} >Galery</a>
                    </li>
                    <li>
                        <a href="/Tarifs_et_Prestations" onClick={menuClick} >Price</a>
                    </li>
                    <li>
                        <a href="/Contact" onClick={menuClick} >Contact</a>
                    </li>
                </ul>
            </div>
            <button onClick={handleClick} className='menu-hamburger'></button>
        </nav>
    )
}
export default Header;
