import React, { useState } from 'react';
const Header = () => {
    const [isActive, setIsActive] = useState(false);

    const toggleClass = event => {

        setIsActive(current => !current);
    };

    return (
        <nav className="navbar">
            <div className="container">
                <div className="nav-elements">
                    <ul className='menu'>
                        <li>
                            <a href="/" id='link1' className={isActive ? 'bg-active' : ''} onClick={toggleClass}>Accueil</a>
                        </li>
                        <li>
                            <a href="/Galerie" id='link2' className='nav-link' onClick={toggleClass}>Galerie</a>
                        </li>
                        <li>
                            <a href="/Tarifs_et_Prestations" id='link3' className='nav-link' onClick={toggleClass}>tarifs et prestations</a>
                        </li>
                        <li>
                            <a href="/Contact" className='nav-link' id='link4' onClick={toggleClass} >Contact</a>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    )

}
export default Header
