import React from 'react';
import { useState } from 'react';
const Header = () => {
    const [isActive, setIsActive] = useState(false);

    const handleClick = (current) => {
        // 👇️ toggle isActive state on click
        setIsActive(current => !current);
    };

    return (
        <nav className="navbar">
            <div className="container">
                <div className="nav-elements">
                    <ul className='menu'>
                        <li>
                            <a href="/" className={isActive ? 'active' : ''} onClick={handleClick}>Accueil</a>
                        </li>
                        <li>
                            <a href="/Galerie" className={isActive ? 'active' : ''} onClick={handleClick}>Galerie</a>
                        </li>
                        <li>
                            <a href="/Tarifs_et_Prestations" className={isActive ? 'active' : ''} onClick={handleClick}>tarifs et prestations</a>
                        </li>
                        <li>
                            <a href="/Contact" className={isActive ? 'active' : ''} onClick={handleClick}>Contact</a>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    )

}
export default Header
