import React from 'react';


export const Header = () => {
    const navLinks = document.querySelector(".nav-links")
    const handleClick = () => {
        navLinks.classList.toggle('mobile-menu')

    };
    var header = document.getElementById("myDIV");
    var btns = header.getElementsByClassName("btn");
    for (var i = 0; i < btns.length; i++) {
        btns[i].addEventListener("click", function () {
            var current = document.getElementsByClassName("active");
            if (current.length > 0) {
                current[0].className = current[0].className.replace(" active", "");
            }
            this.className += " active";
        });
    }
    return (
        <nav className="navbar">
            <div className="nav-links">
                <ul>
                    <li >
                        <a href="/" activeClassName="active" >Home</a>
                    </li>
                    <li>
                        <a href="/Galerie" activeClassName="active" >Galery</a>
                    </li>
                    <li>
                        <a href="/Tarifs_et_Prestations" activeClassName="active" >Price</a>
                    </li>
                    <li>
                        <a href="/Contact" activeClassName="active" >Contact</a>
                    </li>
                </ul>
            </div>
            <button onClick={handleClick} className='menu-hamburger'></button>
        </nav>
    )
}
export default Header;
