import React from 'react';
const Header = () => {
    const current = window.location.href;
    document.querySelectorAll(".menu a").forEach((elem) => {
        if (elem.getAttribute('href').indexOf(current) !== -1) {
            elem.classList.add("active");
        }
    });
    return (
        <nav className="navbar">
            <div className="container">
                <div className="nav-elements">
                    <ul className='menu'>
                        <li >
                            <a href="/" className='nav-link' onclick="makeActive(event)">Accueil</a>
                        </li>
                        <li>
                            <a href="/Galerie" className='nav-link' onclick="makeActive(event)">Galerie</a>
                        </li>
                        <li>
                            <a href="/Tarifs_et_Prestations" className='nav-link' onclick="makeActive(event)">tarifs et prestations</a>
                        </li>
                        <li>
                            <a href="/Contact" className='nav-link' onclick="makeActive(event)" >Contact</a>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    )

}
export default Header
