import React from 'react';


export const Header = () => {
    const navLinks = document.querySelector(".nav-links")
    const handleClick = () => {
        navLinks.classList.toggle('mobile-menu')

    };
    document.querySelectorAll(".nav-item").forEach((ele) =>
        ele.addEventListener("click", function (event) {
            event.preventDefault();
            document
                .querySelectorAll(".nav-item")
                .forEach((ele) => ele.classList.remove("active"));
            this.classList.add("active")
        })
    );
    return (
        <nav className="navbar">
            <div className="nav-links">
                <ul>
                    <li className='nav-item'>
                        <a href="/" className='nav-link'>Home</a>
                    </li>
                    <li className='nav-item'>
                        <a href="/Galerie" className='nav-link' >Galery</a>
                    </li>
                    <li className='nav-item'>
                        <a href="/Tarifs_et_Prestations" className='nav-link' >Price</a>
                    </li>
                    <li className='nav-item'>
                        <a href="/Contact" className='nav-link' >Contact</a>
                    </li>
                </ul>
            </div>
            <button onClick={handleClick} className='menu-hamburger'></button>
        </nav>
    )
}
export default Header;
